import React from 'react';
import Detail from '../Component/Detail';
import { useDispatch } from 'react-redux';
import { addItemToCart } from '../Component/cartSlice';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Electronic = () => {
  const dispatch = useDispatch();

  const products = [
    { name: 'Smart TV', image: 'data:image/jpeg;base64,', price: '30000' },
    { name: 'Bluetooth Speaker', image: 'data:image/jpeg;base64,', price: '5000' },
    { name: 'Gaming Console', image: 'data:image/jpeg;base64,', price: '10000' },
    { name: 'Smartphone', image: 'data:image/jpeg;base64,', price: '25000' },
    { name: 'Refrigirator', image: 'data:image/jpeg;base64,', price: '30000' },
    { name: 'Washing Machine', image: 'data:image/jpeg;base64,', price: '12000' },
  ];

  const handleAddToCart = (product) => {
    dispatch(addItemToCart(product));
    toast.success(`${product.name} added to cart successfully!`);
  };

  return (
    <div>
      {/* Render the Detail component at the top */}
      <Detail />

      {/* Electronic Products Section */}
      <div className="min-h-screen bg-white py-10">
        <div className="container mx-auto">
          <h1 className="text-5xl font-extrabold text-center text-black mb-10">
            Explore Our Electronic Collection
          </h1>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">
            {products.map((product) => (
              <div
                key={product.name} // Use a unique key if possible
                className="relative bg-white rounded-lg overflow-hidden shadow-lg hover:shadow-2xl transition-shadow duration-300 group"
              >
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-64 object-cover transform transition-transform duration-500 group-hover:scale-110"
                />
                <div className="p-6 text-center">
                  <h2 className="text-gray-800 text-2xl font-bold">{product.name}</h2>
                  <p className="text-gray-600 text-xl font-semibold mt-2">Rs{product.price}</p>
                  <button
                    onClick={() => handleAddToCart(product)}
                    className="w-full mt-4 bg-gradient-to-r from-yellow-400 to-red-500 text-white font-bold py-3 px-6 rounded-full hover:from-yellow-500 hover:to-red-600 transition duration-300"
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
        {/* ToastContainer to display toast notifications */}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
};

export default Electronic;
